#include <iostream>
#include <vector>
using namespace std;

class Solution {
public:
    int checkValue(int a, vector<int>& tops, vector<int>& bottoms)
    {
        int n = tops.size();
        int top = 0, bottom = 0;

        for (int i = 0; i < n; i++)
        {
            if (tops[i] != a && bottoms[i] != a) return -1;
            else 
            {
                if (tops[i] != a) top++;
                if (bottoms[i] != a) bottom++;
            }
        }

        return min(top, bottom);
    }

    int minDominoRotations(vector<int>& tops, vector<int>& bottoms) 
    {
        int res_1 = checkValue(tops[0], tops, bottoms);
        int res_2 = checkValue(bottoms[0], tops, bottoms);
        
        if (res_1 == -1) return res_2;
        if (res_2 == -1) return res_1;

        return min(res_1, res_2);
    }
};

